export default {
  name: 'CustomSelect',
  props: {
    items: {
      type: Array,
      required: true
    }
  },
  computed: {
    attrs() {
      return {
        // eslint-disable-next-line vue/no-deprecated-dollar-attrs-api
        ...this.$attrs,
        input: event => this.$emit('input', event.target.value)
      };
    },
    formatedItems() {
      return this.items.map(item => {
        return typeof item === 'object' ? item : {
          value: item,
          label: item
        };
      });
    }
  }
};
