import CustomSelect from '../shared/CustomSelect';
import CustomInput from '../shared/CustomInput';
import SubmitButton from '../shared/Button';
import { isRequired, charLimit } from '../../utils/validationRules';
export default {
  components: {
    CustomSelect,
    CustomInput,
    SubmitButton
  },
  data() {
    return {
      price: '',
      city: ''
    };
  },
  computed: {
    rules() {
      return [isRequired, charLimit(10)];
    },
    cities() {
      return [{
        value: '',
        label: 'Город',
        selected: true
      }, 'Kyiv', 'Odesa', 'Poltava', 'Kharkiv', 'Dnipro', 'Lviv', 'Kherson', 'Mariupol'];
    }
  },
  methods: {
    handleSubmit() {
      this.$emit('submit', {
        city: this.city,
        price: this.price
      });
    }
  }
};
